import { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import { dummy } from './dummy';
import axios from 'axios';

export function TODO(props) {
  const [newTodo, setNewTodo] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [todoData, setTodoData] = useState(dummy);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');

  useEffect(() => {
    const fetchTodo = async () => {
      const apiData = await getTodo();
      setTodoData(apiData);
      setLoading(false);
    };
    fetchTodo();
  }, []);

  const getTodo = async () => {
    const options = {
      method: 'GET',
      url: 'http://localhost:8000/api/todo',
      headers: {
        accept: 'application/json',
      },
    };
    try {
      const response = await axios.request(options);
      return response.data;
    } catch (err) {
      console.log(err);
      return []; // return an empty array in case of error
    }
  };

  const addTodo = () => {
    const options = {
      method: 'POST',
      url: 'http://localhost:8000/api/todo',
      headers: {
        accept: 'application/json',
      },
      data: {
        title: newTodo,
        description: newDescription,
      },
    };
    axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        setTodoData(prevData => [...prevData, response.data.newTodo]);
      })
      .catch(err => {
        console.log(err);
      });
  };

  const deleteTodo = id => {
    const options = {
      method: 'DELETE',
      url: `http://localhost:8000/api/todo/${id}`,
      headers: {
        accept: 'application/json',
      },
    };
    axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        setTodoData(prevData => prevData.filter(todo => todo._id !== id));
      })
      .catch(err => {
        console.log(err);
      });
  };

  const updateTodo = id => {
    const todoToUpdate = todoData.find(todo => todo._id === id);
    const options = {
      method: 'PATCH',
      url: `http://localhost:8000/api/todo/${id}`,
      headers: {
        accept: 'application/json',
      },
      data: {
        ...todoToUpdate,
        done: !todoToUpdate.done,
      },
    };
    axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        setTodoData(prevData => prevData.map(todo => (todo._id === id ? response.data : todo)));
      })
      .catch(err => {
        console.log(err);
      });
  };

  const saveEditTodo = id => {
    const options = {
      method: 'PATCH',
      url: `http://localhost:8000/api/todo/${id}`,
      headers: {
        accept: 'application/json',
      },
      data: {
        title: editTitle,
        description: editDescription,
      },
    };
    axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        setTodoData(prevData => prevData.map(todo => (todo._id === id ? response.data : todo)));
        setEditing(null);
      })
      .catch(err => {
        console.log(err);
      });
  };

  return (
    <div className={Styles.ancestorContainer}>
      <div className={Styles.headerContainer}>
        <h1>Tasks</h1>
        <span>
          <input
            className={Styles.todoInput}
            type='text'
            name='New Todo'
            value={newTodo}
            placeholder='Title'
            onChange={event => {
              setNewTodo(event.target.value);
            }}
          />
          <input
            className={Styles.todoInput}
            type='text'
            name='New Description'
            value={newDescription}
            placeholder='Description'
            onChange={event => {
              setNewDescription(event.target.value);
            }}
          />
          <button
            id='addButton'
            name='add'
            className={Styles.addButton}
            onClick={() => {
              addTodo();
              setNewTodo('');
              setNewDescription('');
            }}
          >
            + New Todo
          </button>
        </span>
      </div>
      <div id='todoContainer' className={Styles.todoContainer}>
        {loading ? (
          <p style={{ color: 'white' }}>Loading...</p>
        ) : todoData.length > 0 ? (
          todoData.map((entry, index) => (
            <div key={entry._id} className={Styles.todo}>
              {editing === entry._id ? (
                <>
                  <input
                    type='text'
                    value={editTitle}
                    onChange={e => setEditTitle(e.target.value)}
                  />
                  <input
                    type='text'
                    value={editDescription}
                    onChange={e => setEditDescription(e.target.value)}
                  />
                  <button onClick={() => saveEditTodo(entry._id)}>Save</button>
                </>
              ) : (
                <>
                  <span className={Styles.infoContainer}>
                    <input
                      type='checkbox'
                      checked={entry.done}
                      onChange={() => {
                        updateTodo(entry._id);
                      }}
                    />
                    {entry.title}
                    <p>{entry.description}</p>
                  </span>
                  <span
                    style={{ cursor: 'pointer' }}
                    onClick={() => {
                      setEditing(entry._id);
                      setEditTitle(entry.title);
                      setEditDescription(entry.description);
                    }}
                  >
                    Edit
                  </span>
                  <span
                    style={{ cursor: 'pointer' }}
                    onClick={() => {
                      deleteTodo(entry._id);
                    }}
                  >
                    Delete
                  </span>
                </>
              )}
            </div>
          ))
        ) : (
          <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
        )}
      </div>
    </div>
  );
}
